import React, {
  useState,
  useEffect
} from 'react';
import * as api from './api';
import './App.css'; // You'll need to create this CSS file

function App() {
  const [session, setSession] = useState(null);
  const [subject, setSubject] = useState('');
  const [rollNo, setRollNo] = useState('');
  const [statusMessage, setStatusMessage] = useState('');

  // Auto-refresh logic
  useEffect(() => {
    let intervalId;
    if (session && session.status === 'active') {
      intervalId = setInterval(() => {
        if (session && session.session_id) {
          fetchSessionDetails(session.session_id);
        }
      }, 10000); // 10 seconds
    }
    return () => clearInterval(intervalId);
  }, [session]);

  const fetchSessionDetails = async (sessionId) => {
    try {
      const details = await api.getSessionDetails(sessionId);
      setSession(details);
    } catch (error) {
      console.error(error.message);
      setStatusMessage(`Error: ${error.message}`);
    }
  };

  // In frontend/src/App.js

const handleStartSession = async () => {
  if (!subject) {
    setStatusMessage('Please enter a subject.');
    return;
  }
  try {
    const newSession = await api.startSession(subject);
    setSession(newSession);
    setStatusMessage('Session started successfully!');

    // ðŸ‘‡ Add this line to fetch full details immediately after starting
    fetchSessionDetails(newSession.session_id);

  } catch (error) {
    console.error(error.message);
    setStatusMessage(`Error: ${error.message}`);
  }
};

  // In frontend/src/App.js

const handleCheckIn = async () => {
  if (!session) {
    setStatusMessage('No active session to check into.');
    return;
  }
  if (!rollNo) {
    setStatusMessage('Please enter a roll number.');
    return;
  }
  try {
    const result = await api.checkIn(session.session_id, rollNo);
    if (result.ok) {
      setStatusMessage(`Check-in successful for ${rollNo}. Total attendees: ${result.total}`);

      // ðŸ‘‡ Add this line to immediately fetch the updated session details
      fetchSessionDetails(session.session_id);

    } else {
      setStatusMessage(`${rollNo} is already checked in.`);
    }
    setRollNo('');
  } catch (error) {
    console.error(error.message);
    setStatusMessage(`Error: ${error.message}`);
  }
};


  const handleEndSession = async () => {
    if (!session) {
      setStatusMessage('No session to end.');
      return;
    }
    try {
      await api.endSession(session.session_id);
      setStatusMessage('Session ended successfully.');
      fetchSessionDetails(session.session_id); // Update status to 'ended'
    } catch (error) {
      console.error(error.message);
      setStatusMessage(`Error: ${error.message}`);
    }
  };

  const isSessionActive = session && session.status === 'active';

  return (
    <div className="app-container">
      <h1>TechMechaTorque Attendance Mock</h1>
      <div className="status-message">
        {statusMessage && <p>{statusMessage}</p>}
      </div>

      {!session ? (
        <div className="session-starter">
          <h2>Start a New Session</h2>
          <input
            type="text"
            placeholder="Subject (e.g., Data Structures)"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
          />
          <button onClick={handleStartSession}>Start Session</button>
        </div>
      ) : (
        <div className="session-details-container">
          <div className="session-info">
            <h2>Session Details</h2>
            <p><strong>Session ID:</strong> {session.session_id}</p>
            <p><strong>Subject:</strong> {session.subject}</p>
            <p><strong>Status:</strong> {session.status}</p>
            <p><strong>Attendees:</strong> {session.attendees_count}</p>
            {isSessionActive && <button className="end-session-btn" onClick={handleEndSession}>End Session</button>}
          </div>

          {isSessionActive && (
            <div className="check-in-section">
              <h3>Check-in</h3>
              <input
                type="text"
                placeholder="Roll No (e.g., 22CSE001)"
                value={rollNo}
                onChange={(e) => setRollNo(e.target.value)}
              />
              <button onClick={handleCheckIn}>Check-in</button>
            </div>
          )}

          <div className="attendees-list">
            <h3>Attendees ({session.attendees_count})</h3>
            <ul>
              {session.attendees && session.attendees.map((attendee, index) => (
                <li key={index}>
                  <strong>{attendee.roll_no}</strong> checked in at {new Date(attendee.time).toLocaleTimeString()}
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
