// public/index.js
const API_BASE = "http://localhost:5000";

async function startSession() {
  const subject = document.getElementById("subjectInput").value;
  const res = await fetch(`${API_BASE}/start-session`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ subject })
  });
  const data = await res.json();
  showSession(data.session);
  showMessage(data.message, "green");
}

async function checkIn() {
  const rollNo = document.getElementById("rollInput").value;
  const res = await fetch(`${API_BASE}/checkin`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ rollNo })
  });
  const data = await res.json();

  if (data.error) {
    showMessage(data.error, "red");
  } else {
    document.getElementById("attendees").textContent = data.attendees.join(", ");
    showMessage(`Checked in! Total attendees: ${data.totalAttendees}`, "green");
  }
}

async function endSession() {
  const res = await fetch(`${API_BASE}/end-session`, { method: "POST" });
  const data = await res.json();

  if (data.error) {
    showMessage(data.error, "red");
  } else {
    showSession(data.session);
    showMessage(data.message, "blue");
  }
}

function showSession(session) {
  if (!session) return;
  document.getElementById("sessionId").textContent = session.id || "";
  document.getElementById("subject").textContent = session.subject || "";
  document.getElementById("status").textContent = session.status || "";
  document.getElementById("attendees").textContent = (session.attendees || []).join(", ");
}

function showMessage(msg, color) {
  const el = document.getElementById("message");
  el.textContent = msg;
  el.style.color = color;
}
