import React, { useState } from "react";

function App() {
  const [session, setSession] = useState(null);
  const [subject, setSubject] = useState("");
  const [rollNo, setRollNo] = useState("");
  const [message, setMessage] = useState("");

  // Start session
  const startSession = async () => {
    try {
      const res = await fetch("http://localhost:5000/start-session", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ subject }),
      });
      const data = await res.json();
      setSession(data.session);
      setMessage(data.message);
    } catch (error) {
      setMessage("Error starting session!");
    }
  };

  // Check-in
  const checkIn = async () => {
    try {
      const res = await fetch("http://localhost:5000/checkin", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ rollNo }),
      });
      const data = await res.json();
      if (data.error) {
        setMessage(data.error);
      } else {
        setSession((prev) => ({
          ...prev,
          attendees: data.attendees,
        }));
        setMessage(`Checked in! Total attendees: ${data.totalAttendees}`);
      }
    } catch (error) {
      setMessage("Error checking in!");
    }
  };

  // End session
  const endSession = async () => {
    try {
      const res = await fetch("http://localhost:5000/end-session", {
        method: "POST",
      });
      const data = await res.json();
      setMessage(data.message);
      setSession(null);
    } catch (error) {
      setMessage("Error ending session!");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Attendance App</h1>

      {!session ? (
        <div>
          <input
            type="text"
            placeholder="Enter Subject"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
          />
          <button onClick={startSession}>Start Session</button>
        </div>
      ) : (
        <div>
          <h2>Session ID: {session.id}</h2>
          <p>Subject: {session.subject}</p>
          <p>Status: {session.status}</p>
          <p>Attendees: {session.attendees?.join(", ") || "None"}</p>

          <input
            type="text"
            placeholder="Enter Roll No"
            value={rollNo}
            onChange={(e) => setRollNo(e.target.value)}
          />
          <button onClick={checkIn}>Check In</button>
          <button onClick={endSession}>End Session</button>
        </div>
      )}

      <p style={{ color: "green" }}>{message}</p>
    </div>
  );
}

export default App;
